    [super viewDidLoad];
    
    CGSize s = self.view.bounds.size;
    
    self.view.backgroundColor = [UIColor whiteColor];
    
    UIView *v1 = [[UIView alloc] init];
    v1.frame = CGRectMake(0, 0, s.width, 50);
    v1.backgroundColor = [UIColor whiteColor];
    v1.autoresizesSubviews = YES;
    v1.autoresizingMask = UIViewAutoresizingFlexibleLeftMargin | UIViewAutoresizingFlexibleWidth | UIViewAutoresizingFlexibleRightMargin;
    
    CALayer *l = [v1 layer];
    [l setShadowColor:[UIColor blackColor].CGColor];
    [l setShadowOffset:CGSizeMake(0.0f, 5.0f)];
    [l setShadowOpacity:0.5f];
    [l setShadowRadius:5.0f];
    [l setMasksToBounds:NO];
    
